import React from "react";
import "./style.css";

const FirstBanner = () => {
  return (
    <div>
      {/* Container */}
      <span style={{ fontWeight: "600" }}>Hôm nay</span>
      <div className="grid-banner">
        {/* GridLayout */}
        <div className="grid-items">
          {/* box 1 */}
          <span className="grid-title">
            <i className="fa-sharp fa-solid fa-user"></i>
            <p>Nhân viên xuất sắc</p>
          </span>
          <span>
            <p>10</p>
            <p>So với hôm qua (+2)</p>
          </span>
        </div>
        <div className="grid-items">
          {/* box 2 */}
          <span>
            <i className="fa-sharp fa-solid fa-user"></i>
            <h5>27</h5>
            <p>Khách hàng mới</p>
          </span>
        </div>
        <div className="grid-items">
          {/* box 3 */}
          <span>
            <i className="fa-sharp fa-solid fa-user"></i>
            <h5>27</h5>
            <p>Số task mới</p>
          </span>
        </div>
      </div>
    </div>
  );
};

export default FirstBanner;
